    public static Dictionary<char, int> CountCharacters(string lorem)
    {
        Dictionary<char, int> loremDict = new Dictionary<char, int>();
        foreach (char character in lorem)
        {
            if (loremDict.ContainsKey(character))
            {
                loremDict[character]++;
            }
            else
            {
                loremDict[character] = 1;
            }
        }
        return loremDict;
    }
